css:
/* mobile last :p */
/* When the browser is at less than 600px */
@media screen and (max-width: 600px) {

  .front-page-menu li {
    line-height: 0;
    padding: 0;
    margin: 0;
}
  
.front-page-menu li a {
    font-size: 4vw;
    line-height: 0;
    padding: 0;
    margin: 0;
}
  
div.r-name {
    font-size: 10px;
    text-align: center;
    width: 100%;
    line-height: 9pt;
}  
  

div.footer-menu li a {
    line-height: 9vw !important;
    font-size: 6vw;
}
  
  .top-site-menu ul {
    font-size: 4vw; 
  }

  
}

body {
    
    background-image: url(/files/covidb1.jpg);
    font-size: 16pt;
    line-height: 28pt;
}

p {
    margin: 0 0 16pt;
}

.l-wrapper {
    max-width: 1300px;
    margin-left: auto;
    margin-right: auto;
}

div.block-hero {
    padding: 0;
    margin: 0;
    border: 0;
}

.top-site-menu ul {
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    width: 90%;
    padding-top: 8px;
    padding-bottom: 9px;
    padding-left: 10px;
    padding-right: 10px;
    margin-bottom: 20px;
  /*  font-size: 2vw;*/
    border-radius: 10px; 
    margin-left: auto;
    margin-right: auto;
}

.top-site-menu li {
    display: inline-block;
    padding: 3px;
    min-width: 2em;
}
.top-site-menu a {
 
   color:#999;
}

@media screen and (min-width: 1300px) {
  .top-site-menu ul {
      font-size: 2.3vw;
  }
  .top-site-menu ul a {
     font-size: 2vw;
    color:#999;
  }
}





.block-menu-menu-footer-menu ul {
    background: rgba(40,40,40,.7);
    width: fit-content;
    padding-top: 11px;
    padding-bottom: 12px;
    padding-left: 20px;
    padding-right: 0px;
    margin-bottom: 20px;
    font-size: 2vw;
    border-radius: 10px;
    margin-left: auto;
    margin-right: auto;
}

.block-menu-menu-footer-menu ul a {
 color:#666; 
}


/* make front page menu items bigger, responsively */
.front-page-menu a {
    font-size: 2.3vw;
  	color:#999;
}
@media screen and (min-width: 1300px) {
  .front-page-menu a {
     font-size: 30px;
    color:#999;
  }
}


.r-photo img {
    border-radius: 20%;
    border: 10px;
    padding: 0px;
    margin: 10px;
    width: 80%;
}

.r-name {
 /*   width: 50%;*/
    text-align: center;
    font-size: x-large;
}

.r-name a {
    color: rgb(150,150,150);
}
.view-band-members {
    padding-bottom: 25px;
}

.front-page-menu li {
    font-size: large;
    min-width: 2em;
    display: inline-block;
    padding: 6px;
    font-size: 2.2vw;
}

.front-page-menu ul {
    text-align: center;
}

.zooming-rat-image p {
    background-image: url(/files/covidb1.jpg);
}


.ds-logo-block {
    position: relative;
    margin-top: -20;
    top: -85px;
    height: 0px;
}

.r-name {
    font-size: 12px;
    text-align: center;
}

.front-page-menu {
    font-size: 14px;
}

/* When the browser is at least 600px and above */
@media screen and (min-width: 600px) {
.ds-logo-block {
    position: relative;
    margin-top: -20;
    top: -315px;
    height: 0px;
}

.r-name {
    font-size: 18px;
    text-align: center;
}

.front-page-menu {
    font-size: 2.2vw;
}

}




ul {
  list-style-type: none;
  margin: 0;
  padding: 0;
  overflow: hidden;
}





